import React from 'react';
import { Filtros } from './Components/Filtros';
import { Products } from './Components/Produtos';
import { ShoppingCart } from './Components/Compras';
import styled from 'styled-components';

const AppContainer = styled.div`
  display: grid;
  grid-template-columns: 1fr 3fr 1fr;
  padding: 16px;
  gap: 8px;
 
`;



const products = [
  {
    id: 1,
    name: 'Carrinho de bebê',
    price: 500,
    photo: 'data:image/jpeg;base64,'
  },
  {
    id: 2,
    name: 'Bola de futebol Nike',
    price: 150,
    photo: 'data:image/jpeg;base64,'
  },
  {
    id: 3,
    name: 'Teclado mecânico Razer',
    price: 780,
    photo: 'data:image/jpeg;base64,'
  },
  {
    id: 4,
    name: 'Bola de Basquete',
    price: 250,
    photo: 'data:image/jpeg;base64,'
  },
  {
    id: 5,
    name: 'Televisão Samsung',
    price: 2500,
    photo: 'data:image/jpeg;base64,'
  },
  {
    id: 6,
    name: 'Mouse Razer',
    price: 400,
    photo: 'data:image/jpeg;base64,'

  }
]

class App extends React.Component {
  state = {
    minFilter: '',
    maxFilter: '',
    nameFilter: '',
    productsInCart: []
    
  }

  onChangeMinFilter = (event) => {
    this.setState({minFilter: event.target.value})
  }

  onChangeMaxFilter = (event) => {
    this.setState({maxFilter: event.target.value})
  }

  onChangeNameFilter = (event) => {
    this.setState({nameFilter: event.target.value})
  }

  onAddProductToCart = (productId) => {
    const productInCart = this.state.productsInCart.find(product => productId === product.id)

    if(productInCart) {
      const newProductsInCart = this.state.productsInCart.map(product => {
        if(productId === product.id) {
          return {
            ...product,
            quantity: product.quantity + 1
          }
        }

        return product
      })

      this.setState({productsInCart: newProductsInCart})
    } else {
      const productToAdd = products.find(product => productId === product.id)

      const newProductsInCart = [...this.state.productsInCart, {...productToAdd, quantity: 1}]

      this.setState({productsInCart: newProductsInCart})
    }
  }

  onRemoveProductFromCart = (productId) => {
    const newProductsInCart = this.state.productsInCart.map((product) => {
      if(product.id === productId) {
        return {
          ...product,
          quantity: product.quantity - 1
        }
      }
      return product
    }).filter((product) => product.quantity > 0)

    this.setState({productsInCart: newProductsInCart})
  }

  render() {
    return (
      <AppContainer>
        <Filtros
          minFilter={this.state.minFilter}
          maxFilter={this.state.maxFilter}
          nameFilter={this.state.nameFilter}
          onChangeMinFilter={this.onChangeMinFilter}            
          onChangeMaxFilter={this.onChangeMaxFilter}            
          onChangeNameFilter={this.onChangeNameFilter}                  
        />
        <Products 
          products={products}
          minFilter={this.state.minFilter}
          maxFilter={this.state.maxFilter}
          nameFilter={this.state.nameFilter}
          onAddProductToCart={this.onAddProductToCart}
        />
        <ShoppingCart
          productsInCart={this.state.productsInCart}
          onRemoveProductFromCart={this.onRemoveProductFromCart}
        />
      </AppContainer>
    );
  }
}

export default App;
